export {};
declare global {
    export namespace BFChainWallet {
        type HostType = {
            ip: string;
            port: number;
        };
        type WalletNode = {
            host: HostType[];
            browserPath?: string;
        };
        type HeadersType = {
            [key: string]: string;
        };
        interface Config {
            bcf: {
                [chainName: string]: {
                    enable: boolean;
                } & WalletNode;
            };
            tron?: {
                enable: boolean;
                host: HostType[];
                headers: HeadersType;
                official?: string;
            };
            eth?: {
                enable: boolean;
                host: HostType[];
                testnet: boolean;
                headers: HeadersType;
                official?: string;
            };
            bsc?: {
                enable: boolean;
                host: HostType[];
                testnet: boolean;
                headers: HeadersType;
                official?: string;
            };
            bscApiScan?: {
                enable: boolean;
                apiHost: string;
                apiKey: string;
            };
            ethApiScan?: {
                enable: boolean;
                apiHost: string;
                apiKey: string;
            };
            tronApiScan?: {
                enable: boolean;
                apiHost: string;
                apiKey: string;
            };
        }
    }
}
